mysql> create table film(film_id smallint unsigned,title varchar(255),description text,release_year year,language_year tinyint unsigned,original_language_id tinyint unsigned,rental_duration tinyint unsigned,rental_rate decima(4,2),length smallint unsigned,replacement_cost decimal(5,2),rating enum('G','PG','PG-13','R','NC-17'),special_features set('Trailers','Commmentaries','Deleted Scenes','Behind the Scenes'),last_update timestamp);